mapper.update:
update user_tb
set password     = #{username},
    user_pic_url = #{password},
    fullname     = #{fullname}
where id = #{id}

update user_tb
set user_pic_url = #{userPicUrl}
where id = #{id}

update user_tb
set password = #{password}
where id = #{id}

mapper.select:
select *
from user_tb
where username = #{username}
  and password = #{password}

SELECT *
FROM user_tb
WHERE user_email = #{userEmail}
  AND user_phone_number = #{userPhoneNumber};
